import React, {HTMLFactory} from "react";
import './code.css'
import {Similarity} from "../file/file-interfaces";

interface CodeProps {
    contents: { [key: string]: string},
    current:string,
    similarities: (Similarity)[]
}
interface CodeState {
    contents: { [key: string]: string}
}

class Code extends React.Component<CodeProps,CodeState>{


    state={
      contents: {},
    }

    componentDidMount() {
        let {current, contents} = this.props

        if(current){
            // setTimeout(() => {
            //     if(this[current]) {
            //         this[current].scrollIntoView()
            //     }
            // }, 10);

        }
        if(contents){
            this.setState({contents})
        }
    }


    componentDidUpdate(prevProps:CodeProps, prevState:CodeState, snapshot:any) {
        let {current, contents} = this.props
        console.log(this)
        if(prevProps && prevProps.current !== current){
            setTimeout(() => {

                if(Code[current]!==null) {

                    this[current].scrollIntoView({behavior:"smooth"})
                }
            }, 10);
        }

        if(prevProps && prevProps.contents !== contents){
            this.setState({contents})
        }

    }




    highlight = (i:number) =>{

        let col = ""
        col = this.props.similarities.some(s => s.startLine <= i && i <= s.endLine) ? "red" : ""
        this.props.similarities.forEach(s => {
            if(s.id === this.props.current && s.startLine <= i && i <= s.endLine){
                col = "red-current"
            }

        })

        return col;
    }

    //map ref to similarity id
    setRef = (i:number, elem:any) =>{
        this.props.similarities.forEach(s => {

            if(i === s.startLine){
                // this[`${s.id}`] = elem

            }})

    }

    //fiiiixxxxx
    // @ts-ignore
    getKeyValue = <T, String extends keyof T>(obj: {}, key: string): T[String] => obj[key];


    render() {

        return <div className="file-container">


            {Object.keys(this.state.contents).map((key, i) => (

                <code key={i} className={this.highlight(i)}
                      ref={elem =>this.setRef(i, {})}>

                    <span className="index" >{i}</span>
                    {this.getKeyValue(this.state.contents, key)}
                 </code>))
            }
        </div>
    }
}


export default Code;
