import React from "react";
import './code.css'
import {Similarity} from "../file/file-interfaces";

interface CodeProps {
    contents: { [key: string]: string},
    current:string,
    similarities: (Similarity)[]
}
interface CodeState {
    contents: { [key: string]: string},
    map: { [key: string]: HTMLElement},

}


class Code extends React.Component<CodeProps,CodeState>{
    private domRefs: any = {}



    componentDidMount() {
        let {current, contents} = this.props

        if(current){
                if(this.domRefs[current]) {

                    this.domRefs[current].scrollIntoView({behavior:"smooth"})
                }
        }
    }


    componentDidUpdate(prevProps:CodeProps, prevState:CodeState, snapshot:any) {
        let {current, contents} = this.props

        if(prevProps.current !== current){


                if(this.domRefs[current]) {
                    this.domRefs[current].scrollIntoView({behavior:"smooth"})
                }

        }


    }




    highlight = (i:number) =>{

        let col = ""
        col = this.props.similarities.some(s => s.startLine <= i && i <= s.endLine) ? "red" : ""
        this.props.similarities.forEach(s => {
            if(s.id === this.props.current && s.startLine <= i && i <= s.endLine){
                col = "red-current"
            }

        })

        return col;
    }

    //map ref to similarity id
    setRef = (i:number, elem:any) =>{
        this.props.similarities.forEach(s => {
            if(i === s.startLine) {
              this.domRefs[`${s.id}`] = elem
            }

       })

    }


    render() {

        return <div className="file-container">
            {Object.keys(this.props.contents).map((key, i) => (

                <code key={i} className={this.highlight(i)}
                      ref={elem =>this.setRef(i, elem)}>
                    <span className="index" >{i}</span>
                    {this.props.contents[key]}
                 </code>))
            }
        </div>
    }
}


export default Code;
